import org.junit.Test;import praktikum.Bun;import static org.junit.Assert.*;public class BunTest {    @Test    public void shouldReturnName(){        Bun bun = new Bun("Name", 10);        String actualResult = bun.getName();        assertEquals("Name", actualResult);    }    @Test    public void shouldReturnPrice(){        Bun bun = new Bun("Name", 10);        float actualResult = bun.getPrice();        assertEquals(10, actualResult, 0);    }}